mapper.select:
SELECT user_no, user_id, username, phone_number, email, role, remaining_seat_time, remaining_study_room_time, remaining_locker_time FROM ( select rownum rn,user_no, user_id, username, phone_number, email, role, remaining_seat_time, remaining_study_room_time, remaining_locker_time FROM ( select rownum rn, user_no, user_id, username, phone_number, email, role, remaining_seat_time, remaining_study_room_time, remaining_locker_time FROM tbl_user order by user_no ) where
rownum <= #{pageNum} * #{amount}
) where rn > (#{pageNum} -1) * #{amount}

mapper.update:
update tbl_user
set password
= #{password}
where user_id
= #{userId}

mapper.select:

   SELECT reservation_no, category, num_using, start_time, duration, name
	FROM (
		SELECT rownum rn, r.*
		FROM (
			SELECT *
			FROM reservation r
			INNER JOIN study_cafe c ON r.cafe_no = c.cafe_no
			WHERE r.user_id = #{userId}
			ORDER BY r.start_time DESC
		) r
		WHERE 



		rownum <= #{pageNum} * #{amount}
	) r
	WHERE rn > (#{pageNum} - 1) * #{amount}
